import './App.css';
import {MicrophoneIcon, SearchIcon } from '@heroicons/react/outline'
import { useState } from 'react';
import axios from "axios";
import firebase from './firebase'
import emailjs from 'emailjs-com';

function App() {

  //using react hooks
  const [url, setUrl] = useState(""); 
  const [number, setNumber] = useState(""); 
  const [email, setEmail] = useState("");



  //handling the API calls
  const handleSubmit = async(event) => {
    event.preventDefault();

    let success = 0;
    let faliures = 0;

    for (var i = 0; i<5; i++){

       function test(i) {
       const inter = setTimeout(() => {
          console.log(success, faliures, i);

          if(faliures > 3){
            console.log("look an error!@!!!");
            delay(inter)
            sendEmail(event)
            return i;
          }
          axios.get("api.openweathermap.org/data/2.5/weather?id=2172797&appid=d6b7df5db8c454548f20c52952a4a1d4")
          .then(()=> {
            success++;
          }).catch(err =>{
            faliures++;
          })
        },i*2000);
      }

     test(i);
  }
}

//stoping the interval timer
function delay(i){
clearTimeout(i)
}


  //handling the onchange events
  const handleChange = (event) =>{
    setUrl(event.target.value);
  }

  const handleLogin = () =>{
    let recaptcha = new firebase.auth.RecaptchaVerifier('recaptcha');
    let number = '+917319368344';
    firebase.auth().signInWithPhoneNumber(number, recaptcha).then(function(e){
      let code = prompt('enter otp', '');
      if(code == null) return;
      e.confirm(code).then(function(result){
        console.log(result.user, 'user');
        alert("Welcome back");
      })
      .catch(err =>{
        console.log(err);
      })
    })
  }

  function sendEmail(e){
    event.preventDefault();
    emailjs.sendForm('service_0itqh7d', 'template_1ig71cm', e.target, 'user_CrKcj2LJr4LH0bMkQRcVD')
    .then((result) => {
        console.log(result.text);
    }, (error) => {
        console.log(error.text);
    });
  } 


  return (
    <div >
      <head>
        <title>Google2.0</title>
        <meta name="description" content="Generated by create next app" />
        <link rel="icon" href="/favicon.ico" />
      </head>
    {/* header-section */}
    <header className="flex w-full p-5 justify-between text-sm text-gray-700">
    {/* div left */}
      <div className="flex space-x-4 items-center">
        <p className="link">About</p>



        <button className="btn"  onClick={handleLogin}>Login</button>


        {/* <p className="link">Store</p> */}

      </div>
    {/* DIV RIGHT */}
      <div className="flex space-x-4 items-center">
        <p className="link">Gmail</p>
        <p className="link">Images</p>

    {/* icons */}


    {/* avatar */}
    {/* <Avatar url="https://i.pinimg.com/474x/7d/1a/3f/7d1a3f77eee9f34782c6f88e97a6c888.jpg"/> */}

      </div> 
    </header>
    {/* body-section */}
    <form className="flex flex-col items-center mt-44 flex-grow" onSubmit={handleSubmit}>
      <img
        src="https://www.google.co.uk/images/branding/googlelogo/2x/googlelogo_color_272x92dp.png"
        width={300}
        height={100}
        />

        <div className="flex w-full mt-5 hover:shadow-lg focus-within:shadow-lg max-w-md rounded-full border border-gray-200 px-5 py-3 items-center sm:max-w-xl">
          <SearchIcon 
            className="h-5 mr-3 text-gray-500"
          />
          <input type="text" className="focus:outline-none flex-grow " value={url} onChange={handleChange}/>
          <MicrophoneIcon className="h-5" />

        </div>

        {/* buttons */}
        <div className="flex flex-col w-1/2 space-y-2 justify-center mt-8 sm:space-y-0 sm:flex-row sm-x-4">
        <button className="btn">Google Search</button>

        </div>

    </form>

    {/* footer-section */}


    </div>
  );
}

export default App;
